import React from 'react';
import NoteDisplay from '../NoteDisplay';

function MyControlledInput() {
  const [value, setValue] = React.useState("");

  const onChange = (event) => {
    setValue(event.target.value);
  };

 const handleClick = () => {
    console.log(value);
  };


  const handleSave = () => {
    localStorage.setItem('blocNote', value);
  }
  console.log(localStorage.getItem('blocNote'))
  return (

          <>
      <div>Input value: {value}</div>

     <NoteDisplay markdownValue = {value} />
      <textarea id="story" name="story"
          rows="5" cols="33" value={value} onChange={onChange}>
<div>save: {localStorage.getItem('blocNote')}</div>
</textarea>

      <button onClick={handleClick}>Envoyer</button>
      <button onClick={handleSave}>Save</button>
    </>

  );

}

export default MyControlledInput;
