  };




  return (
    <div className="form">
       <form>
        <h1>Hello</h1>
        <p>Enter your name:</p>
        <input id="test"
          type="text"
        />
      </form>
      <button onClick={result}>send </button>
     <p>result: {form}</p>
    </div>

  );
};
